import { getDatabase, ref ,push ,onValue} from "firebase/database";
import "./signup.css";
import house from "../imgs/house.png"

function getRandomInt(max) {
    return Math.floor(Math.random() * max);
  }

  function writeUserData( firstname, lastname, email,password,id) {

    try{


    const db = getDatabase();

    push(ref(db, 'users/'), {

      firstname: firstname,
      lastname: lastname,
      email:email,
      password:password,
      id:id
      // profile_picture : imageUrl

    }).then(()=>{
      console.log(firstname,lastname)
      document.querySelector("#firstname").value = "";
      document.querySelector("#lastname").value = "";
      document.querySelector("#email").value = "";
      document.querySelector("#password").value = "";
      document.querySelector("#n2").innerHTML ="Data Sent Successfully "
    }).catch((error)=>{

        console.log("data was not sent ",error)



    })




  }catch(error){
    console.log(error)
    document.querySelector("#n2").innerHTML ="failed to send one or more fields are left Blank "





  }



  }



const getformdata=(e)=>{
    e.preventDefault()
   var firstname= document.querySelector("#firstname").value
    var lastname =document.querySelector("#lastname").value
    var email= document.querySelector("#email").value
    var password =document.querySelector("#password").value




    if(firstname !=="" && lastname !=="" && email!=="" && password!==""){
      const db = getDatabase();
        const starCountRef = ref(db, 'users/');

        onValue(starCountRef,(snapshot)=>{
        snapshot.forEach((element)=>{

          var data =element.val();

          if (email === data.email){
            console.log("this email is already Being Used By someone else ")
            document.querySelector("#n2").innerHTML ="this email is already Being Used By someone else ";
          //  window.location.href="/login";
          }else{
            writeUserData(firstname,lastname,email,password,getRandomInt(5))
            console.log(firstname,lastname,email,password)

            window.location.href="/login";
          }
        })
      })

    }else{
      console.log("one of the fields is Empty")
      console.log(firstname,lastname,email,password)
      document.querySelector("#n2").style.color="red"
      document.querySelector("#n2").innerHTML ="failed to send one or more fields are left Blank "

    }



  }




function Signup() {
    return (
      <div className="div">

<div style={{width: "150px", height: "auto", marginLeft: "auto", marginRight: "auto",}}>
                   <img src={house} width="100%" height="auto" ></img>
            </div>
          <form>
            <h1> Registration Form</h1>
            <h2>Personal Information </h2>
              <p>Firstname</p><input type="text" id="firstname" required></input>


              <p> Lastname </p> <input type="text" id="lastname" required></input>
              <p>Date Of Birth</p><input type="date" id="date" required></input>
              <p>State Of Origin</p>
              <select name="State Of Origin" id="state_of_origin" form="state_of_origin">
                <option value=" Abia"> Abia</option>
                <option value="Adamawa ">Adamawa </option>
                <option value=" Akwa-Ibom"> Akwa-Ibom</option>
                <option value="audi">Anambra</option>
                <option value="Bauchi">Bauchi</option>
                <option value="Bayelsa">Bayelsa</option>
                <option value="Benue ">Benue </option>
                <option value="Borno">Borno</option>
                <option value="Cross River"> Cross River </option>
                <option value="Delta ">Delta </option>
                <option value="Ebonyi ">Ebonyi  </option>
                <option value=" Edo "> Edo </option>
                <option value="Ekiti"> Ekiti</option>
                <option value="Enugu ">Enugu </option>
                <option value="Gombe ">Gombe </option>
                <option value="Imo">Imo</option>
                <option value=" Jigawa"> Jigawa</option>
                <option value="Kaduna">Kaduna</option>
                <option value="Kano">Kano</option>
                <option value="Katsina">Katsina</option>
                <option value=" Kebbi "> Kebbi </option>
                <option value="Kogi ">Kogi </option>
                <option value="Kwara">Kwara</option>
                <option value="Lagos">Lagos</option>
                <option value="Nasarawa"> Nasarawa</option>
                <option value="Niger">Niger</option>
                <option value="Ogun">Ogun</option>
                <option value="Ondo">Ondo</option>
                <option value=" Osun"> Osun</option>
                <option value="Oyo ">Oyo </option>
                <option value="Plateau">Plateau</option>
                <option value="Rivers ">Rivers </option>
                <option value=" Sokoto"> Sokoto</option>
                <option value="Taraba ">Taraba </option>
                <option value="Yobe">Yobe</option>
                <option value="Zamfara">Zamfara</option>

              </select>
                    <p>Job Type</p>            
              <select name="Job Type" id="job_type" form="job_type" required>
                <option value="Partime"> Partime</option>
                <option value="Fulltime">Fulltime</option>
                <option value="Both">Both</option>
                </select>

              <p>Phone Number</p><input type="number" id="Pnumber" required></input>
              <p>Age</p><input type="Number" id="Age" required></input>
              <p> Email </p><input type="email" id="email" required></input>
              <p>Password</p><input type="password" id="password" required></input> 
              <p></p>

              <input type="submit" value="Submit" id="submit_button" onClick={getformdata} style={{padding :"8px", margin:"8px"}}></input>
          </form>



          <div id="n2">

          </div>
          <div id="data">

</div>
      </div>
    );
  }


  export default  Signup;
