import { initializeApp } from 'firebase/app';

// import reactDom from 'react-dom';
import React from 'react';
// For Firebase JavaScript SDK v7.20.0 and later, `measurementId` is an optional field
import { getDatabase, set,ref } from "firebase/database";
  const firebaseConfig = {
    apiKey: "API_KEY",
    authDomain: "PROJECT_ID.firebaseapp.com",
    databaseURL: "https://househelps2-default-rtdb.firebaseio.com/",
    projectId: "househelps2",
    storageBucket: "househelps2.appspot.com",
    messagingSenderId: "SENDER_ID",
    appId: "APP_ID",

  };

  var submit = document.querySelector("#sumbit_button")



  const getformdata=(e)=>{
    e.preventDefault()
   var firstname= document.querySelector("#firstname").value
    var lastname =document.querySelector("#lastname").value

    console.log(firstname,lastname)
    writeUserData(1,firstname,lastname)


  }

  const app = initializeApp(firebaseConfig);
  const database = getDatabase();

  function writeUserData(userId, firstname, lastname, imageUrl) {
    const db = getDatabase();
    set(ref(db, 'users/' + userId), {
      firstname: firstname,
      lastname: lastname,
      // profile_picture : imageUrl

    });

    console.log(firstname,lastname)
  }







  function Form() {
      return (
        <div className="App">
            <form>
                <p>Firstname <input type="text" id="firstname"></input></p>

                <p> Lastname <input type="text" id="lastname"></input></p> 
                <button id="submit_button" onClick={getformdata} style={{padding :"8px", margin:"8px"}}> Enter  </button>
            </form>
        </div>
      );
    }

  export default Form;
