const Partime=()=>{
  const getButtons =()=>{

    setTimeout(()=>{
      var price =0;

      function setprice (array){
        array.forEach((item)=>{
          switch (item) {

            case 'Clean Living Room':
              price = 1000;
              break;
            case 'Wash Bathroom':
              price = 500;
              break;
            case 'Clean Personal Room':
               price = 700;
              break;
            case 'Wash Dishes':
              price =500;
              break;
            case 'Wash Car':
              price = 800;
              break;
            case 'Wash Clothes':
              price = 1000;
              break;
            case "other":
              price = 1000;
          }


        })
        return(price)
      }
      const buttons =document.querySelectorAll(".fresh");
      const selectedChores=[];
      var Amount = 0;
      const btnArry= Array.from(buttons);

      btnArry.forEach((atom)=>{
        atom.style.color="white";
        atom.style.background="green";
        atom.addEventListener("click",()=>{

          if(atom.style.color=="white"){
            console.log("clicked "+atom.innerHTML)
            atom.style.color="green";
            atom.style.background="white";

            selectedChores.push(atom.innerHTML)


           Amount=Amount+ setprice(selectedChores)
           console.log(Amount)


          }else if(atom.style.color=="green"){
            console.log("un clicked "+atom.innerHTML)
            atom.style.color="white";
            atom.style.background="green";
            selectedChores.pop(atom.innerHTML)

           Amount=Amount- setprice(selectedChores)
           console.log(Amount)
          }

        })
      })   
      },500)
  }

  getButtons();






    return(

