        //we no need return for here because we done already catch the data with state and omo your console full o 

      })
    })
   }


  render(){

    return(
      <div>
        {/* howfar run npm start from your own end */}
        {
          this.state.data.map((data,index)=>(
            <div key={index}>
              <p> name:{data.firstname+" "+data.lastname}</p>
              <p> State:{data.state+" "+data.address}</p>
            </div>


    ))

        }

       </div>

    )





}
}






class  Dashboard extends React.Component{
  constructor(){

        super();
        this.state ={
          jobs:{}

        }

    }




    componentDidMount(){
      var view = document.querySelector("#name")
      function load (){
        if(sessionStorage.getItem("name")=="" || sessionStorage.getItem("name")==null ){
