import { initializeApp } from 'firebase/app';


// import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";

// import reactDom from 'react-dom';
import React from 'react';
// For Firebase JavaScript SDK v7.20.0 and later, `measurementId` is an optional field
import { getDatabase, set,ref ,push } from "firebase/database";
  const firebaseConfig = {
    apiKey: "API_KEY",
    authDomain: "PROJECT_ID.firebaseapp.com",
    databaseURL: "https://househelps2-default-rtdb.firebaseio.com/",
    projectId: "househelps2",
    storageBucket: "househelps2.appspot.com",
    messagingSenderId: "SENDER_ID",
    appId: "APP_ID",

  };

  var submit = document.querySelector("#sumbit_button")



  const getformdata=(e)=>{
    e.preventDefault()
   var firstname= document.querySelector("#firstname").value
    var lastname =document.querySelector("#lastname").value
    var email= document.querySelector("#email").value
    var password =document.querySelector("#password").value

    var fval =document.querySelector("#firstname")
    var lval =document.querySelector("#lastname")
    var eeval =document.querySelector("#email")
    var pval =document.querySelector("#password")


    if(firstname !=="" && lastname !=="" && email!=="" && password!==""){
      console.log(firstname,lastname,email,password)
      writeUserData(1,firstname,lastname,email,password)
    }else{
      console.log("one of the fields is Empty")
      console.log(firstname,lastname,email,password)
      document.querySelector("#n2").innerHTML ="failed to send one or more fields are left Blank "
    }



  }

  const app = initializeApp(firebaseConfig);
  const database = getDatabase();

  function writeUserData(userId, firstname, lastname, email,password) {

    try{


    const db = getDatabase();

    push(ref(db, 'users/' + userId), {
      firstname: firstname,
      lastname: lastname,
      email:email,
      password:password
      // profile_picture : imageUrl

    })

    console.log(firstname,lastname)
    document.querySelector("#firstname").value = "";
    document.querySelector("#lastname").value = "";
    document.querySelector("#email").value = "";
    document.querySelector("#password").value = "";
    document.querySelector("#n2").innerHTML ="Data Sent Successfully "


  }catch(error){
    console.log(error)
    document.querySelector("#n2").innerHTML ="failed to send one or more fields are left Blank "





  }



  }



   function App() {
    return (
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/">Home</Link>
              </li>
              <li>
                <Link to="/about">About</Link>
              </li>
              <li>
                <Link to="/users">Users</Link>
              </li>
            </ul>
          </nav>

          {/* A <Switch> looks through its children <Route>s and
              renders the first one that matches the current URL. */}
          <Switch>
            <Route path="/about">
              <About />
            </Route>
            <Route path="/users">
              <Users />
            </Route>
            <Route path="/">
              <Home />
            </Route>
          </Switch>
        </div>
      </Router>
    );
  }




  function About(){
    return(
      <div>
        <h1> About</h1>
      </div>
    )
}


function Users(){
  return(
    <div>
      <h1> Users</h1>
    </div>
  )
}


function Home(){
  return(
    <div>
      <h1>Home</h1>
    </div>
  )
}

  function Form() {
      return (
        <div className="App">
            <form>
              <h2> Sign Up Form</h2>
                <p>Firstname</p><input type="text" id="firstname" required></input>

                <p> Lastname </p> <input type="text" id="lastname" required></input>
                <p> Email </p><input type="email" id="email" required></input>
                <p> Password</p><input type="password" id="password" required></input> 
                <p></p>

                <input type="submit" value="Submit" id="submit_button" onClick={getformdata} style={{padding :"8px", margin:"8px"}}></input>
            </form>



            <div id="n2">

            </div>
        </div>
      );
    }

  export default Form;
