import { initializeApp } from 'firebase/app';
import { getDatabase, ref ,push ,onValue,set} from "firebase/database";
import Header from '../Header';
import React from 'react';
import reactDom from 'react-dom';
import Signup from './Signup';
import Home from './Home';
import Dashboard from '../dashboard/dashboard';
import "./login.css";


  const getformdata2=(e)=>{
    // e.preventDefault()

    var email= document.querySelector("#email").value
    var password =document.querySelector("#password").value

    console.log(email,password)

    if(email !=="" && password !==""){
      document.querySelector("#data").innerHTML ="waiting For Network";
      getDatabasedata(email,password);

      document.querySelector("#email").value= "";
    document.querySelector("#password").value="";
    }else{

      document.querySelector("#data").innerHTML ="incorrect username && password";
    }


  }

  const getDatabasedata=(email,password)=>{

    console.log("passed data "+email,password);


        const db = getDatabase();
        const starCountRef = ref(db, 'users/');

        onValue(starCountRef, (snapshot) => {
        snapshot.forEach((element)=>{


            const data =element.val();

           if(data.email === email && data.password === password){
             console.log("you are now Logged In as "+ data.firstname)

            sessionStorage.setItem("name",data.firstname)
            sessionStorage.setItem('email',data.email)
            sessionStorage.setItem("lastname",data.lastname)
            sessionStorage.setItem("SOO",data.SOO)
            sessionStorage.setItem("age",data.Age)
            sessionStorage.setItem("jobtype",data.jobtype)
            sessionStorage.setItem("pnumber",data.pnumber)


            let props ={
              name:sessionStorage.getItem("name"),
              email:sessionStorage.getItem("email"),
              lastname:sessionStorage.getItem("lastname"),
              SOO:sessionStorage.getItem("SOO"),
              age:sessionStorage.getItem("age"),
              jobtype:sessionStorage.getItem("jobtype"),
              pnumber:sessionStorage.getItem("pnumber")
            };

              renderDashboard(props);
             console.log(data.firstname);


            //  insertdata_dashboard();
           }else{
             console.log("incorrect password or email.")
             document.querySelector("#data").innerHTML="incorrect password or email.";
             document.querySelector("#data").innerHTML ="incorrect password or email.";
           }




          })




        })

          }


      var submit2 = document.querySelector("#sumbit_button_l")

      function renderSignup(){
        reactDom.render(
          <React.StrictMode>
        <Header />
        <Signup />
      </React.StrictMode>,document.querySelector("#root")
        )
      }

      function renderDashboard(Props){
        reactDom.render(
          <React.StrictMode>
        <Header />
        <Dashboard />
      </React.StrictMode>,document.querySelector("#root")
        )
      }


function Login(){

  window.onload =function load(){
    var logologo= document.querySelector("#logo-logo");
    console.log(logologo)
    logologo.style.display="none";


    var span= document.querySelector(".spnn").addEventListener("click",()=>{

      renderSignup();

    })
  }


    return(
        <div className="div">   
            <form name="Login">
            <h1 style={{textAlign:'center'}}>Login</h1>
                <p>Email</p>
                <input type="email" id="email" required></input>

                <p>Password</p>
                <input type="password" id="password" required>
                </input>

                <input type="submit" value="Submit" id="submit_button_l" onClick={getformdata2} style={{padding :"8px", margin:"8px",color:"white", border:"1px solid green",background:"green",borderRadius:"8px"}}></input>
              <div id="data"> </div>
              <div >
              <span className="spnn" style={{color:"green",padding:"20px"}} >dont have an Account ,Signup now</span>
              </div>

            </form>

        </div>

    )

  }



export default Login;
