  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";
import React from 'react';

import house from "./imgs/househelp1.png";
import Header from './Header';
import Home from './home/Home.js';
import Signup from './home/Signup';
import { getDatabase, ref ,push ,onValue} from "firebase/database";


  const firebaseConfig = {
    apiKey: "AIzaSyBk-m6q2o1dKuggqoNxjoKZHmXeMSHhjsA",
    authDomain: "PROJECT_ID.firebaseapp.com",
    databaseURL: "https://househelps2-default-rtdb.firebaseio.com/",
    projectId: "househelps2",
    storageBucket: "househelps2.appspot.com",
    messagingSenderId: "SENDER_ID",
    appId: "APP_ID",

  };

  const app = initializeApp(firebaseConfig);
  // const database = getDatabase();


  var submit = document.querySelector("#sumbit_button")
  var submit2 = document.querySelector("#sumbit_button_l")





  const getformdata2=(e)=>{
    e.preventDefault()

    var email= document.querySelector("#email").value
    var password =document.querySelector("#password").value

    console.log(email,password)

    if(email !=="" && password !==""){
      getDatabasedata(email,password);
      document.querySelector("#data").innerHTML ="waiting For Network";
      document.querySelector("#email").value= "";
    document.querySelector("#password").value="";
    }else{

      document.querySelector("#data").innerHTML ="incorrect username && password";
    }


  }

  const getDatabasedata=(email,password)=>{

    console.log("passed data "+email,password);


        const db = getDatabase();
        const starCountRef = ref(db, 'users/');

        onValue(starCountRef, (snapshot) => {
        snapshot.forEach((element)=>{


            const data =element.val();

           if(data.email === email && data.password === password){
             console.log("you are now Logged In as "+ data.firstname)

            sessionStorage.setItem("name",data.firstname)
            sessionStorage.setItem('email',data.email)
            sessionStorage.setItem('alldata',[data.email,data.firstname,data.lastname,data.SOO,data.Age,data.jobtype,data.pnumber])
            console.log(sessionStorage.getItem("alldata"));


              window.location.href="/Dashboard";
             console.log(data.firstname);


             insertdata_dashboard();
           }else{
             console.log("incorrect password or email.")
           }




          })




        })

          }

      const  insertdata_dashboard=()=>{
        var name = sessionStorage.getItem("name")
        console.log(name);

        var view = document.getElementById("#name");

        console.log(view)
      }








   function App() {
    return (
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/Login"></Link>
              </li>

              <li>
                <Link to="/Signup"></Link>
              </li>
              <li>
                <Link to="/Dashboard"></Link>
              </li>
              <li>
                <Link to="/"></Link>
              </li>
            </ul>
          </nav>

          <Switch>

            <Route path="/Signup">
              <Signup />
            </Route>


            <Route path="/Login">
              <Login />
            </Route>

            <Route path="/Dashboard">
              <Dashboard />
            </Route>
            <Route exact path="/">
               <Home />
            </Route>



          </Switch>
        </div>
      </Router>
    );
  }






function Login(){
  return(
      <div className="div">   
          <form name="Login">
          <h1 style={{textAlign:'center'}}>Login</h1>
              <p>email</p>
              <input type="email" id="email"></input>

              <p>Password</p>
              <input type="password" id="password">
              </input>

              <input type="submit" value="Submit" id="submit_button_l" onClick={getformdata2} style={{padding :"8px", margin:"8px",color:"white", border:"1px solid green",background:"green",borderRadius:"8px"}}></input>
            <div id="data"> </div>
          </form>
      </div>
  )

}


  function Dashboard(){

    window.onload = function load (){
      if(sessionStorage.getItem("name")=="" || sessionStorage.getItem("name")==null ){
        window.location.href="/Signup";
      }
      var view = document.querySelector("#name")

      console.log(view);

      var Username =sessionStorage.getItem("name");
     Username= Username+"";
     Username.toUpperCase();
      console.log(Username);

      view.innerHTML = sessionStorage.getItem("name").toUpperCase()+ sessionStorage.getItem("alldata");
      console.log(sessionStorage.getItem("alldata"))
    }


    return(
      <div>
        <h2 style={{textAlign:"center"}}>Hello, welcome Agent <span id="name"> </span> </h2>
        <p className="names" id="nn3"> </p>
      </div>
    );
  }
  export default App;
