import React from 'react';
import styles from './customer.css';
import apartstyle from "./apartment.css";
import Header from '../../Header';
import reactDom from "react-dom";
import leftarrow from "../../imgs/g_left.png";
import { Partime,Request } from '../Request';
import house from "../../imgs/logo.png";
// import { useFlutterwave, closePaymentModal } from 'flutterwave-react-v3';







function Customer(){


    const GetCustomerDetails =()=>{

        var Name =document.querySelector("#customername").value;
        var Email =document.querySelector("#customeremail").value;
        var Number =document.querySelector("#customernumber").value;
        var Address =document.querySelector("#customeraddress").value;


        if(Name!=="" && Email !=="" && Number !=="" && Address !==""){
            console.log(Name,Email,Number,Address);


            var config = {
              public_key: "FLWPUBK-d414dfaf4d80cb19a871956045965e25-X",
              tx_ref: Date.now(),
              amount: sessionStorage.getItem("amount"),
              currency: 'NGN',
              payment_options: 'card,mobilemoney,ussd,bank',
              customer: {
                email: Email,
                phonenumber: Number,
                name:Name,
              },
              customizations: {
                title: '@Househelp.ng',
                description: 'Payment househelp to @househelp.ng',
                logo: {house},
              },
            };
            function makePayment() {
              FlutterwaveCheckout({
                public_key: "FLWPUBK-d414dfaf4d80cb19a871956045965e25-X",
                tx_ref: "hooli-tx-1920bbtyt",
              //   amount: amount,
              amount:sessionStorage.getItem("amount"),
                currency: "NGN",
                country: "NG",
                payment_options: "card, mobilemoneyghana, ussd",
                redirect_url: // specified redirect URL
                  "./",
                meta: {
                  consumer_id: 23, 
                  consumer_mac: "92a3-912ba-1192a",
                },
                customer: {
                  email: Email,
                  phone_number: Number,
                  name: Name,
                },
                callback: function (data) {
                  console.log(data);
                },
                onclose: function() {
                  // close modal
                },
                customizations: {
                  title: "Househelp.ng",
                  description: "Payment for House chores selected",
                  logo: "https://househelp.netlify.app/logo.png",
                },
              });
            }





        }else{
            console.log("empty Field Detected")

        }


    }
    setTimeout(function(){

        var submit19 = document.querySelector(".submit19")

        submit19.addEventListener("click",()=>{ 

             GetCustomerDetails();

        })

    },2000)


    return(
        <div style={{textAlign:"center"}} style={styles}>

             <div style={{width:"70px",height:"auto", marginLeft:"auto",marginRight:"auto"}}onClick={renderPartime} >
                <img src={leftarrow} style={{width:"100%", color:"green"}}></img>
                </div>
            <h2>Enter your Information</h2>
            <form >

            <label>Name</label><input type="text" id="customername" required></input>
            <label>Email</label><input type="email" id="customeremail" required></input>
            <label>Phone Number</label><input type="Phonenumber" id="customernumber" required></input>
            <label>Full House Address</label><input type="Address" id="customeraddress" required></input>
            <script src="https://checkout.flutterwave.com/v3.js"></script>
            <input type="submit"  className="submit19" ></input>

            </form>

        </div>
    )
}



function Apartment(){

    setTimeout(()=>{
        var apartments = document.querySelectorAll(".apartment")
        console.log(apartments);
        var appartmentarray=Array.from(apartments)
        console.log(appartmentarray)

        appartmentarray.forEach((element)=>{
            element.addEventListener("click",()=>{
                console.log("clicked "+element.innerHTML)
                var multiple;
                switch (element.innerHTML) {

                    case "Self Contain Apartment":
                        multiple=1
                        sessionStorage.setItem("multiple",multiple)
                        renderPartime()
                        break;

                    case "1 Bedroom Self contain":
                    multiple=1
                    sessionStorage.setItem("multiple",multiple)
                    renderPartime()
                    break;  
                    case "2 Bedrooms Self contain":
                    multiple=1.5
                    sessionStorage.setItem("multiple",multiple)
                    renderPartime()
                    break;  
                    case "2 Bedrooms (family)":
                    multiple=2
                    sessionStorage.setItem("multiple",multiple)
                    renderPartime()
                    break;  
                    case "3 Bedrooms Flat":
                    multiple=3
                    sessionStorage.setItem("multiple",multiple)
                    renderPartime()
                    break; 
                    case "3 Bedrooms Flat (family)":
                        multiple=4
                        sessionStorage.setItem("multiple",multiple)
                        renderPartime()
                        break;  
                        case "duplex":
                        multiple=6
                        sessionStorage.setItem("multiple",multiple)
                        renderPartime()
                        break;    

                    default:
                        break;
                }
            })
        })

    },100)

    console.log(sessionStorage.getItem("amount"))
    return(<div>
        <br></br>
            <div style={{width:"70px",height:"auto", marginLeft:"auto",marginRight:"auto"}}onClick={renderRequest} >
                <img src={leftarrow} style={{width:"100%", color:"green"}}></img>
                </div>
                <br></br>
        <div style={apartstyle} className="apartdiv">
            <h2>select the size of our apartment</h2>
            <button className="apartment apartment-1">Self Contain Apartment</button>
            <button className="apartment apartment-2">1 Bedroom Self contain</button>
            <button className="apartment apartment-3">2 Bedrooms Self contain</button>
            <button className="apartment apartment-4">2 Bedrooms (family)</button>
            <button className="apartment apartment-4">3 Bedrooms Flat</button>
            <button className="apartment apartment-5">3 Bedrooms Flat (family)</button>
            <button className="apartment apartment-6">duplex</button>
        </div>
        </div>
    )
}

function renderApartment(){


    reactDom.render(
      <div><Header /> <Apartment /></div>,
      document.querySelector("#root")
    )
  }
  function renderPartime(){


    reactDom.render(
      <div><Header /> <Partime /></div>,
      document.querySelector("#root")
    )
  }

  function renderRequest(){
    reactDom.render(
      <div>
        <Header />
        <Request />
      </div>


  ,document.querySelector("#root")
    )
  }


export  {Customer,Apartment};
