import React from "react";
import { Fulltime } from "../request/Request";
import jobliststyle from "./joblist.css";
import { useEffect } from "react";
const Joblist=({firstname,state,address,lastname,pnumber,jobtype,email,middlename,index,date})=>{


  useEffect(() => {
  var btns = Array.from(document.querySelectorAll(".job-apply-btn"))
   var joblistdiv= Array.from(document.querySelectorAll("#joblistdiv"));

   joblistdiv.forEach((element,i)=>{
     var newclassname ="job-apply-btn-"+i;
     element.classList.add(newclassname)

   })

  // function addlistener(element,index) {
  //   element.addEventListener("click",()=>{
  //     console.log("clicked btn "+ index)
  //     // console.log(document.querySelector(".job-apply-btn-"+index).querySelector("#card-details"))
  //   })

  // }
    var appliedjobs =[];

  btns.forEach((element,index)=>{
    var newclassname ="job-apply-btn-"+index;
    element.classList.add(newclassname)
    element.style.color="white";
    element.style.background="green";

    element.addEventListener("click",()=>{


      if(element.style.color==="white"){
        console.log("clicked btn " + index)
        appliedjobs.push(newclassname)
        element.style.color="green";
        element.style.background="white";
      }else if( element.style.color="green"){
        console.log("unclicked btn " + index)
        appliedjobs.pop(newclassname)
        element.style.color="white";
        element.style.background="green";
      }

    })

  })

    })


        return(
            <div id="joblistdiv" style={jobliststyle} key={index}>
              <div id="cardheader" style={{width:"100%", color:"green"}}> 
              <h2 style={{textAlign:"left"}}>State: {state}</h2>
              <button id="" className="job-apply-btn" style={{width:"100%",margin:"0"}}>Apply</button>
              </div>

              <div id="card-details">
                <table>
                 <th></th>
                  <tr>
                  <td>FullName:</td>
                    <td>{firstname +"  "+ " "+lastname+" "+" "+ middlename}</td>
                  </tr>
                  <tr>
                    <td>Email</td>
                    <td>{email}</td>
                  </tr>
                  <tr>
                    <td>Address:</td>
                    <td>{address +"  "+" "}</td>

                  </tr>
                  <tr>
                    <td>Application Date:</td>
                    <td>{date }</td>

                  </tr>
                  <tr>
                    <td>Jobtype</td>
                    <td>{jobtype}</td>
                  </tr>
                </table>
              </div>


            </div>
          )






}

export default Joblist;
