import { initializeApp } from 'firebase/app';
import "./form.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import React from 'react';
import { getDatabase, ref ,push ,onValue} from "firebase/database";
  const firebaseConfig = {
    apiKey: "API_KEY",
    authDomain: "PROJECT_ID.firebaseapp.com",
    databaseURL: "https://househelps2-default-rtdb.firebaseio.com/",
    projectId: "househelps2",
    storageBucket: "househelps2.appspot.com",
    messagingSenderId: "SENDER_ID",
    appId: "APP_ID",

  };

  const app = initializeApp(firebaseConfig);
  const database = getDatabase();


  var submit = document.querySelector("#sumbit_button")
  var submit2 = document.querySelector("#sumbit_button_l")

  function getRandomInt(max) {
    return Math.floor(Math.random() * max);
  }


  const getformdata=(e)=>{
    e.preventDefault()
   var firstname= document.querySelector("#firstname").value
    var lastname =document.querySelector("#lastname").value
    var email= document.querySelector("#email").value
    var password =document.querySelector("#password").value




    if(firstname !=="" && lastname !=="" && email!=="" && password!==""){
      console.log(firstname,lastname,email,password)
      writeUserData(firstname,lastname,email,password,getRandomInt(5))
      window.location.href="/login";
    }else{
      console.log("one of the fields is Empty")
      console.log(firstname,lastname,email,password)
      document.querySelector("#n2").innerHTML ="failed to send one or more fields are left Blank "
    }



  }

  const getformdata2=(e)=>{
    e.preventDefault()

    var email= document.querySelector("#email").value
    var password =document.querySelector("#password").value

    console.log(email,password)

    if(email !=="" && password !==""){
      getDatabasedata(email,password);
      document.querySelector("#data").innerHTML ="waiting For Network";
      document.querySelector("#email").value= "";
    document.querySelector("#password").value="";
    }else{

      document.querySelector("#data").innerHTML ="incorrect username && password";
    }


  }

  const getDatabasedata=(email,password)=>{

    console.log("passed data "+email,password);


        const db = getDatabase();
        const starCountRef = ref(db, 'users/');

        onValue(starCountRef, (snapshot) => {
        snapshot.forEach((element)=>{


            const data =element.val();

           if(data.email === email && data.password === password){
             console.log("you are now Logged In as "+ data.firstname)
            //  document.querySelector("#name").innerHTML=data.firstname;
             window.location.href="/Dashboard";
             console.log(data.firstname);
           }else{
             console.log("incorrect password or email.")
           }




          })




        })

          }





  function writeUserData( firstname, lastname, email,password,id) {

    try{


    const db = getDatabase();

    push(ref(db, 'users/'), {

      firstname: firstname,
      lastname: lastname,
      email:email,
      password:password,
      id:id
      // profile_picture : imageUrl

    }).then(()=>{
      console.log(firstname,lastname)
      document.querySelector("#firstname").value = "";
      document.querySelector("#lastname").value = "";
      document.querySelector("#email").value = "";
      document.querySelector("#password").value = "";
      document.querySelector("#n2").innerHTML ="Data Sent Successfully "
    }).catch((error)=>{

        console.log("data was not sent ",error)



    })




  }catch(error){
    console.log(error)
    document.querySelector("#n2").innerHTML ="failed to send one or more fields are left Blank "





  }



  }



   function App() {
    return (
      <Router>
        <div>
          <nav>
            <ul>
              <li>
                <Link to="/Login">Login</Link>
              </li>

              <li>
                <Link to="/Signup">Sign up </Link>
              </li>
              <li>
                <Link to="/Dashboard"></Link>
              </li>
            </ul>
          </nav>

          <Switch>

            <Route path="/Signup">
              <Form />
            </Route>


            <Route path="/Login">
              <Login />
            </Route>

            <Route path="/Dashboard">
              <Dashboard />
            </Route>



          </Switch>
        </div>
      </Router>
    );
  }






function Login(){
  return(
      <div className="div"> 

          <form name="Login">
          <h1>Login</h1>
              <p>email</p>
              <input type="email" id="email"></input>

              <p>Password</p>
              <input type="password" id="password">
              </input>

              <input type="submit" value="Submit" id="submit_button_l" onClick={getformdata2} style={{padding :"8px", margin:"8px",color:"black", border:"1px solid black",borderRadius:"8px"}}></input>
            <div id="data"> </div>
          </form>
      </div>
  )

}

  function Form() {
      return (
        <div className="div">
            <form>
              <h2> Sign Up Form</h2>
                <p>Firstname</p><input type="text" id="firstname" required></input>

                <p> Lastname </p> <input type="text" id="lastname" required></input>
                <p> Email </p><input type="email" id="email" required></input>
                <p> Password</p><input type="password" id="password" required></input> 
                <p></p>

                <input type="submit" value="Submit" id="submit_button" onClick={getformdata} style={{padding :"8px", margin:"8px"}}></input>
            </form>



            <div id="n2">

            </div>
            <div id="data">

</div>
        </div>
      );
    }
  function Dashboard(){
    return(
      <div>
        <h2>Hello, welcome <span id="name"></span> </h2>
      </div>
    )
  }
  export default App;
